        uint64 userId,
        uint96 buyAmount,
        uint96 sellAmount
    ) internal pure returns (bytes32) {
        return
            bytes32(
                uint256(userId) *
                    2**192 +
                    uint256(buyAmount) *
                    2**96 +
                    uint256(sellAmount)
            );
    }
